var wrap = new Vue({
    el: '#wrap',
    data: {
        h_list: htmlData.reverse(),
        c_list: cssData.reverse(),
        j_list: jsData.reverse(),
        m_list: miscData.reverse(),
        a_list: appData.reverse(),
        r_list: resData,
        hk_list: hikData.reverse(),
        query: "",
        isActive: 1,
        navActive: false,
        listActive: false,
        cleanActive: false,
        currentFilter: 'all',
    },

    methods: {
        // 取得 list 的數據，給分頁指定用
        getHtmlIndex({ h_list = [], index = 0 }) {
            return h_list[index] || {}
        },

        getCssIndex({ c_list = [], index = 0 }) {
            return c_list[index] || {}
        },

        getJsIndex({ j_list = [], index = 0 }) {
            return j_list[index] || {}
        },

        getMiscIndex({ m_list = [], index = 0 }) {
            return m_list[index] || {}
        },

        getAppIndex({ a_list = [], index = 0 }) {
            return a_list[index] || {}
        },

        getHikIndex({ hk_list = [], index = 0 }) {
            return hk_list[index] || {}
        },

        // 重置搜尋框的值
        resetSearchInput: function(e) {
            e.preventDefault()
            this.$data.cleanActive = false;
            this.$data.query = "";
            // document.getElementById("cleanBtn").classList.remove("is-active");
        },

        // 篩選
        setFilter: function(filter) {
            this.currentFilter = filter;
        }
    },

    computed: {
        filterHtml: function() {
            var search = this;
            return this.h_list.filter(function(item) {
                return (item.title.toLowerCase().indexOf(search.query.toLowerCase()) !== -1 || item.tag1_name.toLowerCase().indexOf(search.query.toLowerCase()) !== -1 || item.tag2_name.toLowerCase().indexOf(search.query.toLowerCase()) !== -1);
            });
        },

        filterCss: function() {
            var search = this;
            return this.c_list.filter(function(item) {
                return (item.title.toLowerCase().indexOf(search.query.toLowerCase()) !== -1 || item.tag1_name.toLowerCase().indexOf(search.query.toLowerCase()) !== -1 || item.tag2_name.toLowerCase().indexOf(search.query.toLowerCase()) !== -1);
            });
        },

        filterJs: function() {
            var search = this;
            return this.j_list.filter(function(item) {
                return (item.title.toLowerCase().indexOf(search.query.toLowerCase()) !== -1 || item.tag1_name.toLowerCase().indexOf(search.query.toLowerCase()) !== -1 || item.tag2_name.toLowerCase().indexOf(search.query.toLowerCase()) !== -1);
            });
        },

        filterMisc: function() {
            var search = this;
            return this.m_list.filter(function(item) {
                return (item.title.toLowerCase().indexOf(search.query.toLowerCase()) !== -1 || item.tag1_name.toLowerCase().indexOf(search.query.toLowerCase()) !== -1 || item.tag2_name.toLowerCase().indexOf(search.query.toLowerCase()) !== -1);
            });
        },

        filterApp: function() {
            var search = this;
            return this.a_list.filter(function(item) {
                return (item.title.toLowerCase().indexOf(search.query.toLowerCase()) !== -1 || item.tag1_name.toLowerCase().indexOf(search.query.toLowerCase()) !== -1 || item.tag2_name.toLowerCase().indexOf(search.query.toLowerCase()) !== -1);
            });
        },

        filterRes: function() {
            var search = this;
            return this.r_list.filter(function(item) {
                return (item.title.toLowerCase().indexOf(search.query.toLowerCase()) !== -1 || item.tag.toLowerCase().indexOf(search.query.toLowerCase()) !== -1);
            });
        },

        filterHik: function() {
            var search = this;
            return this.hk_list.filter(function(item) {
                return (item.title.toLowerCase().indexOf(search.query.toLowerCase()) !== -1 || item.tag1_name.toLowerCase().indexOf(search.query.toLowerCase()) !== -1 || item.tag2_name.toLowerCase().indexOf(search.query.toLowerCase()) !== -1);
            });
        },
        
    },

    watch: {
        // 監聽搜尋框的值 一有變化就執行判斷式
        query: function(val, oldVal) {
            if(document.getElementById("searchInput").value.length > 0){
                // document.getElementById("cleanBtn").classList.add("is-active");
                this.cleanActive = true;
            } else {
                // document.getElementById("cleanBtn").classList.remove("is-active");
                this.cleanActive = false;
            }
        }
    },
})