<aside class="float-wrap">
    <a href="#0" class="btn-contact"></a>
    <a href="#0" class="btn-top"></a>
    <button class="float-close"></button>
</aside>
<div class="f-contact-wrap">
	<div class="f-contact-main">
		<div class="f-contact-content">
			<div class="f-contact-head">Send us a message</div>
			<div class="f-contact-form">
                <div class="f-contact-input">
                    <input type="text" id="name" name="name">
                    <label for="name">Name</label>
                </div>
                <div class="f-contact-input">
                    <input type="text" id="company" name="company">
                    <label for="company">Compamy</label>
                </div>
                <div class="f-contact-input">
                    <input type="text" id="subject" name="subject">
                    <label for="subject">Subject</label>
                </div>
                <div class="f-contact-input">
                    <input type="text" id="mail" name="mail">
                    <label for="mail">E-mail</label>
                </div>
                <div class="f-contact-input">
                    <input type="text" id="tel" name="tel">
                    <label for="tel">Telephone</label>
                </div>
                <div class="f-contact-input">
                    <textarea id="msg" name="msg"></textarea>
                    <label for="msg">Message</label>
                </div>
			</div>
			<div class="contact-form-tool">
                <hgroup>
                    <p><input type="checkbox"><span>Send Copy To Self</span></p>
                    <div class="upload">
                        <label for="fileUpload" class="custom-file-upload">Attach A File</label>
                        <input id="fileUpload" type="file"/>
                    </div>
                </hgroup>
                <button>SEND MESSAGE</button>
            </div>
		</div>
		<button class="overlay-close"></button>
	</div>
</div>