    <script>
        $(function(){
            $('.carousel-wrap').owlCarousel({
                nav: true,
                dots: false,
                loop: false,
                autoWidth: true,
                mouseDrag: false,
                touchDrag: true,
            })
        });
        $(function(){
            $(".carousel-item:first").addClass("active");
            $(".carousel-content:first").addClass("active");
            $(".carousel-item").click(function(){
                $(this).addClass("active");
                $(this).siblings().removeClass("active");
                $(".carousel-content").siblings().removeClass("active");  
                $(".carousel-content").eq($(this).index()).addClass("active");
            });
        });
    </script>
