    <script src="js/script.js"></script>
    <script>
        $(".product-title").on("click", function(e){
            if($(this).parent().has(".product-content")) {
                e.preventDefault();
            }
            if(!$(this).hasClass("active")) {
                $(".product-content").slideUp();
                $(".product-title").removeClass("active");
                $(this).next(".product-content").slideDown();
                $(this).addClass("active");
            }
            else if($(this).hasClass("active")) {
                $(this).removeClass("active");
                $(this).next(".product-content").slideUp();
            }
        });
    </script>
