<!-- JS -->
<script src="js/popupmenu.js"></script>
<script>
    $(function() {
        $(".device-news-title").on("click", function(e){
            if($(this).parent().has(".device-news-content")) {
                e.preventDefault();
            }
            if(!$(this).hasClass("active")) {
                $(".device-news-content").slideUp();
                $(".device-news-title").removeClass("active");
                $(this).next(".device-news-content").slideDown();
                $(this).addClass("active");
            }
            else if($(this).hasClass("active")) {
                $(this).removeClass("active");
                $(this).next(".device-news-content").slideUp();
            }
        });
    });
</script>