                        <label><i class="far fa-calendar-minus"></i></label>
                        <p class="label-error"></p>
                    </div>
                </form>
            </div>
            <div class="bet-record-filter">
                <nav>
                    <span class="active" data-name="all">全部显示</span>
                    <span data-name="settlement">已结算</span>
                    <span data-name="unsettlement">未结算</span>
                </nav>
