    <!-- Alert window -->
    <div class="alert-overlay">
        <div class="alert-content">
            <h2>请选转您的萤幕</h2>
            <p>为了让您有更良好的游戏体验，我们由衷建议您将装置萤幕转为横向，谢谢。</p>
        </div>
    </div>
    <!-- Fullcsreen ctrl -->
    <div class="fullscreen-wrap">
        <span id="fullScreen" class="f-btn full active">全螢幕模式</span>
        <span id="cancelFullScreen" class="f-btn cancel">關閉全螢幕</span>
    </div>
    
    <script src="js/script.js"></script>
    <script src="js/screenfull.min.js"></script>
    <script>
        $(document).ready(function(){
            $('#fullScreenSupport').html(
            screenfull.enabled
            ? '瀏覽器支援全螢幕API'
            : '瀏覽器不支援全螢幕API');
        });

        $('#fullScreen').click(function() {
            if (screenfull.enabled) {
                screenfull.request();
            }
            $(this).removeClass('active');
            $('#cancelFullScreen').addClass('active');
        });

        $('#cancelFullScreen').click(function() {
            if (screenfull.enabled) {
                screenfull.exit();
                $('#fullScreen').addClass('active');
                $('#cancelFullScreen').removeClass('active');
            }
        });
    </script>
