    <!-- JS -->
    <script src="js/script.js"></script>
    <script>
        $(".news-title").on("click", function(e){
            if($(this).parent().has(".news-content")) {
                e.preventDefault();
            }
            if(!$(this).hasClass("is-active")) {
                $(".news-content").slideUp();
                $(".news-title").removeClass("is-active");
                $(this).next(".news-content").slideDown();
                $(this).addClass("is-active");
            }
            else if($(this).hasClass("is-active")) {
                $(this).removeClass("is-active");
                $(this).next(".news-content").slideUp();
            }
        });
        $(".news-close").on("click", function(e){
            $(this).parent().slideUp();
            $(this).parent().prev(".news-title").removeClass("is-active");
        });
    </script>
