    <script>
        $(function(){
            var $block = $('.product-view-tabs');
            $('.product-view-tabs .tabs-title ul li').click(function(){
                var $this = $(this);
                $this.add($('.tabs-main .tabs-content', $block)
                    .eq($this.index()))
                    .addClass('active')
                    .siblings('.active')
                    .removeClass('active');
            });
        });

        $(function() {
            $(".product-aside h2").click(function (){
                $('.product-aside-group').addClass('active');
                $('.product-aside-overlay').addClass('active');
                $('html, body').addClass('no-scroll');
            });
            $(".product-aside-close").click(function (){
                $('.product-aside-group').removeClass('active');
                $('.product-aside-overlay').removeClass('active');
                $('html, body').removeClass('no-scroll');
            });
        });
    </script>
