    <script>
        // Tabs
        $(function(){
            $(".view-tabs-item:first").addClass("active");
            $(".view-tabs-content:first").addClass("active");
            $(".view-tabs-item").click(function(){
                $(this).addClass("active");
                $(this).siblings().removeClass("active");
                $(".view-tabs-content").siblings().removeClass("active");  
                $(".view-tabs-content").eq($(this).index()).addClass("active");
                $(".receive-content").removeClass("active");
            });
        });
        // quantity
        $('.quantity').each(function() {
            var spinner = $(this),
            input = spinner.find('input[type="number"]'),
            btnUp = spinner.find('.btn-up'),
            btnDown = spinner.find('.btn-down'),
            min = input.attr('min'),
            max = input.attr('max');

            btnUp.click(function() {
                var oldValue = parseFloat(input.val());
                if (oldValue >= max) {
                    var newVal = oldValue;
                } else {
                    var newVal = oldValue + 1;
                }
                spinner.find("input").val(newVal);
                spinner.find("input").trigger("change");
            });

            btnDown.click(function() {
                var oldValue = parseFloat(input.val());
                if (oldValue <= min) {
                    var newVal = oldValue;
                } else {
                    var newVal = oldValue - 1;
                }
                spinner.find("input").val(newVal);
                spinner.find("input").trigger("change");
            });
        });
    </script>
    <script src="js/thumbs.min.js"></script>
