                pager: true,
                controls: true,
                auto: true,
                pause: 6000,
                moveSlides: 1,
                infiniteLoop: true,
                speed: 1200,
            });
        });
        $(function(){
            $("#liveTabs .tabs-item:first, #casinoTabs .tabs-item:first, #sportTabs .tabs-item:first, #lotteryTabs .tabs-item:first").addClass("active");
            $("#liveTabs .tabs-content:first, #casinoTabs .tabs-content:first, #sportTabs .tabs-content:first, #lotteryTabs .tabs-content:first").addClass("active");
            $("#liveTabs .tabs-item").click(function(){
                $(this).addClass("active");
                $(this).siblings().removeClass("active");
                $("#liveTabs .tabs-content").siblings().removeClass("active");  
                $("#liveTabs .tabs-content").eq($(this).index()).addClass("active");
            });
            $("#casinoTabs .tabs-item").click(function(){
                $(this).addClass("active");
                $(this).siblings().removeClass("active");
                $("#casinoTabs .tabs-content").siblings().removeClass("active");  
                $("#casinoTabs .tabs-content").eq($(this).index()).addClass("active");
            });
            $("#sportTabs .tabs-item").click(function(){
                $(this).addClass("active");
                $(this).siblings().removeClass("active");
                $("#sportTabs .tabs-content").siblings().removeClass("active");  
                $("#sportTabs .tabs-content").eq($(this).index()).addClass("active");
            });
            $("#lotteryTabs .tabs-item").click(function(){
                $(this).addClass("active");
                $(this).siblings().removeClass("active");
                $("#lotteryTabs .tabs-content").siblings().removeClass("active");  
                $("#lotteryTabs .tabs-content").eq($(this).index()).addClass("active");
            });
        });
    </script>
