    <script src="js/script.js"></script>
    <script>
        $(".p-title").on("click", function(e){
            if($(this).parent().has(".p-sub")) {
                e.preventDefault();
            }
            if(!$(this).hasClass("active")) {
                $(".p-sub").slideUp();
                $(".p-title").removeClass("active");
                $(this).next(".p-sub").slideDown();
                $(this).addClass("active");
            }
            else if($(this).hasClass("active")) {
                $(this).removeClass("active");
                $(this).next(".p-sub").slideUp();
            }
        });
        $(".btn-aside").click(function (){
            $('html, body').addClass("no-scroll");
            $('.products-aside').addClass("active");
        });
        $(".btn-close").click(function (){
            $('html, body').removeClass("no-scroll");
            $('.products-aside').removeClass("active");
        });
    </script>
